  const Ctime=()=>{
timezone(new Date().toLocaleTimeString());
setInterval(Ctime, 1000);
  }
  return(
    <>
      <h1>{time}</h1>
      <button onClick={Ctime}>
        :Click:
      </button>
    </>
  );
}
export default Time;
